import pycomimport micropythonimport networkimport machineimport timepycom.heartbeat(False)pycom.rgbled(0x000000)while True:    # https://forum.pycom.io/topic/226/lopy-adcs    adc = machine.ADC()    apin = adc.channel(pin='P16')    value = apin.value()            # read value, 0-4095    ris=str(value)    print(ris)    #----------------------------------------------    # write on file    f = open('/flash/battery_level.txt', 'a+')    f.write("batt[{}]\n".format(ris))    f.close()    pycom.rgbled(0xff0000)    time.sleep(1)    pycom.rgbled(0x00ff00)    time.sleep(1)    #----------------------------------------------